# Import the pyqtconfig.py for the correct python3 version and normal or debug
# build.

import sys

if sys.version_info[1] == 6:
    if 'd' in sys.abiflags:
        try:
            from PyQt4.pyqtconfig_d6 import *
            from PyQt4.pyqtconfig_d6 import _pkg_config, _default_macros
        except ImportError as msg:
            raise ImportError('No module named pyqtconfig; package python3-pyqt4-dbg not installed')
    else:
        from PyQt4.pyqtconfig_nd6 import *
        from PyQt4.pyqtconfig_nd6 import _pkg_config, _default_macros

